select --patientId res String 是 患者ID to_char(pat.PATIENTID) AS patientId, --patientName res String 是 患者姓名 pat.NAME AS patientName, --gender res String 是 用户性别：M-男性 F-女性 decode(pat.SEX,1,'M',2,'F',to_char(pat.SEX)) AS gender, --healthCardNo res String 是 健康卡号码 pat.HCNO AS healthCardNo, --idCardNo res String 否 身份证 pat.IDNO AS idCardNo, --birthday res String 否 出生日期 to_char(pat.BIRTHPLACE, 'yyyy-MM-dd') AS birthday, --oppatNo res String 否 患者病历号 '-' AS oppatNo, --phone res String 否 联系电话 pat.MOBILE AS phone, --medicareCardNo res String 否 医保卡号 '-' AS medicareCardNo, --homeAddress res String 否 家庭住址 pat.ADDRESS AS homeAddress, --remark res String 否 备注 '-' AS remark, --feeType res String 否 结算类型代码，1.医保 2自费 3公医 4省医 decode(pat.PAYORID, 3,'2',4,'3',9,'4',to_char(pat.PAYORID)) AS feeType, --userTag res String 否 用户标签 '-' AS userTag from PA_DATA_PATIENT pat
and pat.IDNO = #{idCardNo}
AND pat.HCNO = #{healthCardNo}
AND to_char(pat.PATIENTID) = #{patientId}

select --clinicSeq payListInfo String 是 就诊流水号 to_char(cf.ENCOUNTERID) AS clinicSeq, --clinicTime payListInfo String 是 就诊日期 to_char(cf.INPUTTIME,'yyyy-MM-dd') AS clinicTime, --hospitalId payListInfo String 是 医院代码 '-' AS hospitalId, --deptId payListInfo String 是 接诊科室代码 to_char(cf.DEPTID) AS deptId, --deptName payListInfo String 是 接诊科室名称 (select DEPTNAME from OM_DEPARTMENT where DEPTID=cf.DEPTID ) AS deptName, --doctorId payListInfo String 是 接诊医生代码 to_char(cf.DOCID) AS doctorId, --doctorName payListInfo String 是 接诊医生姓名 (select USERNAME from SM_USER where USERID= cf.DOCID) AS doctorName, --payAmout payListInfo String 是 未缴费总金额，单位：分 (select to_char( sum( nvl( decode( pce.ISCHARGE , '1' , pce.AMOUNT,0 ),0))) from PC_CL_DATA_RECIPEENTRY pce where pce.RECIPEID = cf.RECIPEID) AS payAmout, --settleCode payListInfo String 是 结算类型代码，自费00 '00' AS settleCode, --必填参数，，否则页面会显示null --settleType payListInfo String 否 结算类型名称,自费时可空 '' AS settleType, --outpatientType payListInfo String 否 诊间支付类型： （空字符串或0：普通线上诊间支付模式；1：充值模式） '' AS outpatientType, --prescriptionIds payListInfo String 否 处方号 to_char( cf.RECIPEID ) AS prescriptionIds, --isFirstPay payListInfo String 否 是否优先支付 1是0 否 '' AS isFirstPay -- from pc_cl_data_recipe cf , pa_data_patient br where cf.PATIENTID = br.PATIENTID and not exists ( select cf1.recipeid from pc_cl_data_recipe cf1 where cf1.ENCOUNTERID = cf.ENCOUNTERID and nvl(cf1.SOCIALTYPE,'9') != '9' and cf1.isdelete = '0' and cf1.ischarge = '0' ) --处方中的自费 and br.ISDELETE = '0' and cf.ISDELETE = '0' and cf.isCharge = '0' and to_char(cf.INPUTTIME,'yyyy-mm-dd') > to_char(sysdate - #{limitDay} , 'yyyy-mm-dd')
and to_char(cf.INPUTTIME,'yyyy-mm-dd') >= #{beginDate}
and to_char(cf.INPUTTIME,'yyyy-mm-dd') <= #{endDate}
and br.HCNO = #{cardNo} order by cf.PATIENTID,cf.ENCOUNTERID,cf.RECIPEID

select a.encounterid AS encounterid, a.RECIPEID AS recipeid, b.ITEMID AS typeCode, (select ITEMNAME from AB_ORDERITEM where ITEMID = b.ITEMID) AS typeName, to_char(nvl( decode( b.ISCHARGE , '1' , b.AMOUNT,0 ),0)) AS typeAmout
from PC_CL_DATA_RECIPE a , PC_CL_DATA_RECIPEENTRY b where a.recipeid = b.recipeid and a.RECIPEID in
#{recId}

select to_char( wm_concat(a.RECIPEID) ) from PC_CL_DATA_RECIPE a where RECIPEID in
#{recNo}
group by a.ENCOUNTERID

select * from PC_CL_DATA_RECIPEENTRY where RECIPEID in

    #{oneDrRec}

and ischarge = '1'

select 100 AS discountrate, cft.deptid AS deptid, cft.docid AS docid, 0 AS discountsum, xm.clcatalogid AS cataid, sum(cfmx.amount) AS totalsum, sum(cfmx.amount) AS spsum, sysdate AS inputtime, sum( nvl( cfmx.IMMAMOUNT , 0 ) ) AS immtotalsum from PC_CL_DATA_RECIPEENTRY cfmx , PC_CL_DATA_RECIPE cft ,ab_orderitem xm where cfmx.recipeid = cft.recipeid and cfmx.ischarge = '1' and cfmx.itemid = xm.itemid and cft.recipeid in
#{oneDrRec}
group by cft.deptid, cft.docid , xm.clcatalogid

select xm.clinvoicecataid AS cataid, nvl( sum(cfmx.amount),0) AS amount, nvl(sum(cfmx.IMMAMOUNT),0) AS immamount from PC_CL_DATA_RECIPEENTRY cfmx , PC_CL_DATA_RECIPE cft ,ab_orderitem xm where cfmx.recipeid = cft.recipeid and cfmx.ischarge = '1' and cfmx.itemid = xm.itemid and cft.recipeid in
#{oneDrRec}
group by xm.clinvoicecataid

select distinct '0' as HISREQUESTID , '1' as TRUSTID , to_char(cf.RECIPEID) as REQUESTNO , to_char(mx.entryid) as HISID , to_char(cf.encounterid) as PATIENTNO , br.name as PATIENTNAME , br.hcno as RXNO , '1' as PATIENTTYPE , to_char(br.sex) as PATIENTSEX , br.idno as PATIENTIDCARD , br.mobile as MOBILETELEPHONE , br.BIRTHDAY as PATIENTBIRTHDAY , to_char(cf.DEPTID) as DEPTCODE , (select deptname from om_department where deptid = cf.deptid and rownum < 2) as DEPTNAME , to_char(cf.docid) as DRCODE , (select username from sm_user where userid = cf.docid and rownum < 2) as DRNAME , '' as DIAGNAME , '' as BEDNO , '' as REQUESTAIM , to_char(mx.itemid) as ITEMCODE , (select GROUPCODE from ab_ordergroup where groupid = mx.itemid) as ITEMCODE , (select GROUPNAME from ab_ordergroup where groupid = mx.itemid) as ITEMNAME , (select to_char(nvl(sum(mx3.amount), 0)) from pc_cl_data_recipeentry mx3 where mx3.ischarge = '1' and mx3.recipeid = cf.recipeid and mx3.groupno = mx.groupno) as ITEMPRICE , '' as SAMPLENAME , '0' as TRANSTTAG , decode(instr(nvl(mx.note, 'null'), 'T'), 0, '1', '3') as PRI , '0' as SENDDRMESSAGE , '0' as SENDPATIENTMESSAGE , '0' as SENDLABMESSAGE , sysdate as REQUESTTIME , mx.note as NOTE , '-1' as CHARGEID from pc_cl_data_recipeentry mx , pc_cl_data_recipe cf , pa_data_patient br , pc_cl_data_recipeentry mx2 where cf.RECIPEID = mx.RECIPEID and br.patientid = cf.patientid and mx2.itemsource = 10 and mx2.entrytype = 7 and mx.recipeid = mx2.recipeid and mx.entrytype = 99 and mx.groupno = mx2.groupno and mx2.recipeid in
#{oneDrRec}
union all select distinct '0' as HISREQUESTID , '1' as TRUSTID , to_char(cf.RECIPEID) as REQUESTNO , to_char(mx.entryid) as HISID , to_char(cf.encounterid) as PATIENTNO , br.name as PATIENTNAME , br.hcno as RXNO , '1' as PATIENTTYPE , to_char(br.sex) as PATIENTSEX , br.idno as PATIENTIDCARD , br.mobile as MOBILETELEPHONE , br.BIRTHDAY as PATIENTBIRTHDAY , to_char(cf.DEPTID) as DEPTCODE , (select deptname from om_department where deptid = cf.deptid and rownum < 2) as DEPTNAME , to_char(cf.docid) as DRCODE , (select username from sm_user where userid = cf.docid and rownum < 2) as DRNAME , '' as DIAGNAME , '' as BEDNO , '' as REQUESTAIM , to_char(mx.itemid) as ITEMCODE , (select itemcode from ab_orderitem where itemid = mx.itemid) as ITEMCODE , (select itemname from ab_orderitem where itemid = mx.itemid) as ITEMNAME , (select to_char(nvl(sum(mx3.amount), 0)) from pc_cl_data_recipeentry mx3 where mx3.ischarge = '1' and mx3.recipeid = cf.recipeid and mx3.groupno = mx.groupno) as ITEMPRICE , '' as SAMPLENAME , '0' as TRANSTTAG , decode(instr(nvl(mx.note, 'null'), 'T'), 0, '1', '3') as PRI , '0' as SENDDRMESSAGE , '0' as SENDPATIENTMESSAGE , '0' as SENDLABMESSAGE , sysdate as REQUESTTIME , mx.note as NOTE , '-1' as CHARGEID from pc_cl_data_recipeentry mx, pc_cl_data_recipe cf, pa_data_patient br where cf.RECIPEID = mx.RECIPEID and br.patientid = cf.patientid and mx.itemsource = 1 and mx.entrytype = 7 and mx.recipeid in
#{oneDrRec}

select --doctorId doctorInfo String 是 医生ID use.USERID AS doctorId, --doctorName doctorInfo String 是 医生名称 use.USERNAME AS doctorName, --doctorLevelCode doctorInfo String 是 医生职称代码 to_char(usi.RANKID ) AS doctorLevelCode, --doctorLevel doctorInfo String 是 医生职称 (SELECT RANKNAME FROM OM_RANK WHERE RANKID = usi.RANKID) AS doctorLevel, --deptId doctorInfo String 是 科室代码 to_char(usl.LOCID) AS deptId, --deptName doctorInfo String 是 科室名称 dept.DEPTNAME AS deptName --description doctorInfo String 否 医生简介 from SM_USER use ,SM_USERINFO usi , SM_USERLOC usl, OM_DEPARTMENT dept WHERE use.USERID = usi.USERID AND use.USERID = usl.USERID AND usl.LOCID = dept.DEPTID AND usl.ISDEFAULT = '1'
AND usl.LOCID = #{deptId}
AND usl.LOCID = #{doctorId}

select --healthCardNo req String 否 患者卡号*/ clr.HCNO AS healthCardNo, --clinicSeq req String 否 就诊流水*/ to_char(clr.ENCOUNTERID) AS clinicSeq, --patientId req String 否 患者ID*/ to_char(clr.PATIENTID) AS patientId, --orderId req String 否 移动订单号*/ clr.BOOKID AS orderId, --orderDate req String 否 挂号日期【格式：YYYY-MM-DD】*/ to_char(clr.REGTIME, 'yyyy-MM-dd') AS orderDate, --visitDate req String 否 就诊日期【格式：YYYY-MM-DD】*/ to_char(clr.INPUTTIME, 'yyyy-MM-dd') AS visitDate, --patientName req String 否 患者姓名*/ clr.NAME AS patientName, --idCardNo req String 否 身份证号码*/ clr.IDNO AS idCardNo, --bookingNo req String 否 HIS预约订单号/当天挂号锁号ID*/ -- AS bookingNo, --doctorId req String 否 医生ID*/ to_char(clr.REGDOCTOR) AS doctorId, --orderStatus req String 否 挂号状态：* 0：未取号 * 1：已取号* 2：已退费(已取号)* 3: 已就诊* 4：未付费取消* 5: 已付费已取消* 6: 允许预约报到* 7：已爽约* */ DECODE(clr.ENCOUNTERSTATUS) AS orderStatus,
--/**status 是否已取号：（当处理结果代码为0时，该字段必输） 0：未取号 1：已取号 2：已退费(已取号) 3: 已就诊4：未付费取消 5: 已付费已取消 6: 允许预约报到 7：已爽约8：可签到（需为已支付） 9：已签到 case when clr.isCharge = '0' and clr.BOOKSTATUS = 4 then '4' when clr.isCharge = '1' and clr.BOOKSTATUS = 4 then '5' when clr.ISBOOK = '1' and clr.isCharge = '0' and clr.bookStatus = 1 then '6' when clr.ISBOOK = '1' and clr.bookStatus = 3 then '7' when clr.ISBOOK = '1' and clr.isCharge = '1' and clr.bookStatus = 1 then '8' when clr.ISBOOK = '1' and clr.bookStatus = 2 then '9' when clr.encounterStatus = 2 and vis.VISITSTATUS then '3' end AS status, --/**healthCardNo orderInfo String 是 患者卡号*/ clr.HCNO AS healthCardNo, --/**patientName orderInfo String 是 患者姓名*/ clr.NAME AS patientName, --/**patientId orderInfo String 是 患者ID*/ to_char(clr.PATIENTID) AS patientId, --/**orderId orderInfo String 是 移动订单号*/ clr.BOOKID AS orderId, --/**bookingNo orderInfo String 是 HIS预约订单号/当天挂号锁号ID*/ to_char(clr.ENCOUNTERID) AS bookingNo, --/**clinicSeq orderInfo String 是 就诊流水号*/ to_char(clr.ENCOUNTERID) AS clinicSeq, --/**hospitalId orderInfo String 否 医院代码 第三方挂的号要在服务平台支付，则该参数必传*/ --AS hospitalId, --/**deptId orderInfo String 是 科室代码*/ to_char(clr.REGDEPT) AS deptId, --/**deptName orderInfo String 是 科室名称*/ (select DEPTNAME from OM_DEPARTMENT where DEPTID = clr.REGDEPT)AS deptName, --/**doctorId orderInfo String 否 接诊医生代码*/ to_char(clr.REGDOCTOR) AS doctorId, --/**doctorName orderInfo String 否 接诊医生姓名*/ (select USERNAME from SM_USER where USERID = clr.REGDOCTOR)AS doctorName, --/**regDate orderInfo String 否 就诊日期*/ to_char(clr.REGTIME, 'yyyy-MM-dd') AS regDate, --/**shiftCode orderInfo String 否 班别代码*/ --AS shiftCode, --/**shiftName orderInfo String 否 班别名称，如上午、下午*/ --AS shiftName, --/**startTime orderInfo String 否 分时开始时间，格式：HH:MI*/ --AS startTime, --/**endTime orderInfo String 否 分时结束时间，格式：HH:MI*/ --AS endTime, --/**svObjectId orderInfo String 否 服务对象id，默认为普通患者*/ --AS svObjectId, --/**svObjectName orderInfo String 否 患者服务对象(人群 )*/ --AS svObjectName, --/**scheduleId orderInfo String 否 排班号*/ --AS scheduleId, --/**roomAddress orderInfo String 否 出诊位置*/ --AS roomAddress, --/**registerType orderInfo String 是 挂号类型 0-预约挂号 1-当天挂号*/ decode(clr.REGTYPE, 6,'1','0')AS registerType, --/**queueNo orderInfo String 是 排队序号*/ to_char(vis.dayDeptNo)AS queueNo, --/**waitingCount orderInfo Sring 否 前面就诊人数，如果患者已就诊，则设为-1*/ --AS waitingCount, --/**waitingTime orderInfo Sring 否 预计候诊等待时间(分钟) 例:1患者就诊设置3分分钟，10个患者预计候诊30分钟*/ --AS waitingTime, --/**infoTime orderInfo String 否 取号时间格式： YYYY-MM-DD HI24:MI:SS*/ --AS infoTime, --/**visitTime orderInfo String 否 就诊时间（实际接诊时间） YYYY-MM-DD HI24:MI:SS*/ --AS visitTime, --/**orderType orderInfo String 是 挂号来源 10-金蝶微信 11-金蝶支付宝（其他渠道的值his自定义）*/ '-' AS orderType, --/**orderTypeName orderInfo String 否 挂号来源名称*/ --AS orderTypeName, --/**orderTime orderInfo String 是 挂号下单时间*/ to_char(clr.INPUTTIME, 'yyyy-MM-dd hh:mm:ss') AS orderTime, --/**remark orderInfo String 否 备注*/ --AS remark, --/**isCancelabe orderInfo String 是 是否可以取消，0-不可以，1-可以，2-未定义，移动平台可根据自己的规则控制。*/ '2'AS isCancelabe, --/**isPayment orderInfo String 否 是否允许支付【0-不允许；1-允许】 默认值为1*/ --AS isPayment , --/**payStatus orderInfo String 是 支付状态 0-未支付 1-已支付 2-已退费*/ --AS payStatus, --/**regFee orderInfo String 是 挂号费(单位“分”)*/ --AS regFee, --/**treatFee orderInfo String 是 诊疗费(单位“分”)*/ --AS treatFee, --/**yhFee orderInfo String 是 优惠挂号费(单位“分”)*/ --AS yhFee, --/**payFee orderInfo String 是 实际支付费用(单位“分”) payFee = regFee + treatFee - yhFee*/ --AS payFee, --/**isSelectSvObject orderInfo String 否 是否允许选择服务对象 0-不允许，1-允许默认值为0,该参数只针对第三方渠道的挂号订单，在支付前选择服务对象。*/ --AS isSelectSvObject, --/**autoLockBeforePay orderInfo String 否 挂号支付前，是否必须先锁号 0-不允许，1-允许默认值为0，该参数只针对第三方渠道的挂号订单。*/ --AS autoLockBeforePay, -- from PA_CL_DATA_REGISTER clr ,PA_CL_DATA_VISITQUEUE vis where clr.ENCOUNTERID = vis.ENCOUNTERID(+)
AND clr.HCNO = #{healthCardNo}
AND clr.ENCOUNTERID = #{clinicSeq}
AND clr.PATIENTID = #{patientId}
AND clr.BOOKID = #{orderId}
AND to_char(clr.INPUTTIME, 'yyyy-MM-dd') = #{orderDate}
AND to_char(clr.REGTIME, 'yyyy-MM-dd') = #{visitDate}
AND clr.NAME = #{patientName}
AND clr.IDNO = #{idCardNo}
AND to_char(clr.REGDOCTOR) = #{doctorId}
AND clr.ENCOUNTERSTATUS = #{orderStatus}

select --deptId deptInfo String 是 科室代码*/ to_char(dept.DEPTID)AS deptId, --deptName deptInfo String 是 科室名称*/ dept.DEPTNAME AS deptName, --deptType deptInfo Integer 是 1专科2 专家*/ '-' AS deptType, --parentId deptInfo String 是 上级科室代码,没有填-1*/ to_char(dept.parentID) AS parentId, --description deptInfo String 是 科室简介 */ '-' AS description, --orderNo deptInfo String 否 排序号 to_char(dept.SORT) AS orderNo, --keyDeptLevel deptInfo String 否 重点科室标示 --01，国家临床重点专科； --02，国家区域中医诊疗中心； --03，国家中医重点专科； --04，省重点中医专科 '-' AS keyDeptLevel from OM_DEPARTMENT dept , OM_DEPARTMENTVISIT depv where dept.ISDELETE = '0' and dept.DEPTID = depv.DEPTID and depv.VISITTYPE in (1,2)
AND dept.DEPTID = #{deptId}
GROUP BY dept.DEPTID,dept.DEPTNAME,dept.parentID,dept.SORT

select --doctorId regInfo String 是 医生代码 to_char(us.USERID) AS doctorId, --doctorName regInfo String 是 医生名称 us.NAME AS doctorName, --doctorLevelCode regInfo String 是 医生职称代码 TO_CHAR(usin.RANKID) AS doctorLevelCode, --doctorLevel regInfo String 是 医生职称 (select RANKNAME from OM_RANK where RANKID = usin.RANKID) AS doctorLevel, --description regInfo String 否 医生简介 --affiliatedHospital regInfo String 否 医生所属医院 --registerMode regInfo String 否 挂号模式 --0：普通模式，需要及时支付确认 --1：审核模式：预约成功后，需要医生助手确认通过才能支付 --remark regInfo String 否 医生出诊信息备注 --deptAddress scheduleInfo String 否 出诊诊室地址
--deptId scheduleInfo String 是 科室代码 to_char(us.LOCID) AS deptId, --deptName scheduleInfo String 是 科室名称 (select DEPTNAME from OM_DEPARTMENT where DEPTID = us.LOCID) AS deptName, --roomAddress scheduleInfo String 否 出诊位置 --clinicUnitName scheduleInfo String 否 诊疗单元名称 --clinicUnitId scheduleInfo String 否 诊疗单元代码 --regDate scheduleInfo String 是 出诊日期，格式：YYYY-MM-DD to_char(res.WORKDATE, 'yyyy-MM-dd') AS regDate, --shiftCode scheduleInfo String 是 班别代码 CASE when res.ENDTIME < '12:00' then 'am' when res.STARTTIME > '12:00' then 'pm' end AS shiftCode, --shiftName scheduleInfo String 是 班别名称，如上午、下午 CASE when res.ENDTIME < '12:00' then '上午' when res.STARTTIME > '12:00' then '下午' end AS shiftName, --startTime scheduleInfo String 否 班别开始时间，格式：HH:MI res.STARTTIME AS startTime, --endTime scheduleInfo String 否 班别结束时间，格式：HH:MI res.ENDTIME AS endTime, --scheduleId scheduleInfo String 否 排班号 res.SUBSCRIBENO AS scheduleId, --regStatus scheduleInfo String 是 出诊状态 --0-停诊 --1-出诊 --2-暂未开放 '1' AS regStatus, --regTotalCount scheduleInfo String 是 该时段可预约的总号源数 '1' AS regTotalCount, --regLeaveCount scheduleInfo String 是 该时段剩余号源数 decode(res.ISUSE,'1','0','0','1') AS regLeaveCount, --regFee scheduleInfo String 是 挂号费(单位“分”) '-' AS regFee, --treatFee scheduleInfo String 是 诊疗费(单位“分”) '-' AS treatFee, --isTimeReg scheduleInfo String 是 是否有分时 --0- 否1- 是 '1' AS isTimeReg, --hasRegisterType scheduleInfo String 否 是否有班别类型选择，空表示否 --0-否 --1-是 '1' AS hasRegisterType
from DGSTYYYPT_DATA_RESOURCE res ,DGSTYYYPT_USER us, SM_USERINFO usin where res.PRODUCERID = us.PRODUCERID and us.USERID = usin.USERID
AND us.LOCID = #{deptId}
AND us.USERID = #{doctorId}
AND us.NAME like '%'+#{searchCode}+'%'
AND to_char(res.WORKDATE, 'yyyy-MM-dd') > #{startDate}
AND to_char(res.WORKDATE, 'yyyy-MM-dd') < #{endDate}

--2.1.2.2 住院患者信息查询 select --patientId recordInfo String 是 住院患者唯一ID to_char(ipr.PATIENTID) AS patientId, --hospitalId recordInfo String 是 院区代码 '-' AS hospitalId, --patientName recordInfo String 是 姓名 ipr.NAME AS patientName, --inTime recordInfo String 是 入院日期 to_char(ipr.INDATE, 'yyyy-MM-dd') AS inTime, --outTime recordInfo String 是 出院日期(未出院，为空) to_char(ipr.OUTDATE, 'yyyy-MM-dd') AS outTime, --inDays recordInfo String 是 住院天数 to_char(ipr.ipCount) AS inDays, --patientFlag recordInfo String 是 患者状态:在院/预出院/结账/出院/清账 decode(ipr.ENCOUNTERSTATUS, 2,'在院',3,'出院') AS patientFlag, --totalAmout recordInfo String 是 总费用（单位分） ( select nvl(sum(b.AMOUNT),0) from AB_IP_DATA_BILL b where b.encounterid = ipr.encounterid ) AS totalAmout, --prepayAmout recordInfo String 是 预交金额（单位分） ( select nvl(sum( d.amount ),0) from AB_IP_DATA_DEPOSIT d where d.encounterid = ipr.encounterid and d.depositStatus = 1 ) as prepayAmout, --balance recordInfo String 是 预交金余额（单位分） ( select nvl(sum( d.amount ),0) from AB_IP_DATA_DEPOSIT d where d.encounterid = ipr.encounterid and d.depositStatus in ( 1 , 3 ) ) as balance, --settled recordInfo String 否 已结算总额（单位分） '' AS settled, --inpatientId recordInfo String 是 住院号 ipr.ENCOUNTERID AS inpatientId, --idCardNo recordInfo String 否 患者身份证件号码 ipr.IDNO AS idCardNo, --deptId recordInfo String 是 病区代码 to_char(ipr.INDEPT) AS deptId, --deptName recordInfo String 是 病区 nvl( (select DEPTNAME from om_department where deptid = ipr.indept and rownum < 2 ),'无') as deptName, --bedNo recordInfo String 否 床位 nvl( (select to_char(bedid) from PA_IP_DATA_WARDPATIENT where encounterid = ipr.encounterid and ISCURRENT = '1' and rownum < 2 ),'无') as bedNo, --chargeDoctorId recordInfo String 是 主管医生代码 to_char(ipr.inDoctor) AS chargeDoctorId, --chargeDoctorName recordInfo String 是 主管医生姓名 (select USERNAME from SM_USER where USERID = ipr.inDoctor)AS chargeDoctorName, --chargeNurseId recordInfo String 是 主管护士代码 to_char(ipw.RESPONSENURSE) AS chargeNurseId, --chargeNurseName recordInfo String 是 主管护士姓名 (select USERNAME from SM_USER where USERID = ipw.RESPONSENURSE)AS chargeNurseName, --gender recordInfo String 否 性别（男、女） (Select dictname from sm_dictionary where dicttype=4 and OID = ipr.SEX) AS gender, --birthday recordInfo String 否 出生日期 to_char(ipr.BIRTHDAY, 'yyyy-MM-dd') AS birthday, --address recordInfo String 否 家庭地址 ipr.CENSUSADDR AS address, --connectPhone recordInfo String 否 联系电话（患者电话） ipr.MOBILE AS connectPhone, --relatePerson recordInfo String 否 联系人 ipr.contact AS relatePerson, --relation recordInfo String 否 联系人关系（文字） (select relationName from Pa_Relation where ISDELETE = '0' and RELATIONID = ipr.relation) AS relation, --relatePhone recordInfo String 否 联系人电话 ipr.contactTele AS relatePhone, --remark recordInfo String 否 备注 '-'AS remark, --isReadyCopy recordInfo String 否 是否允许复印 '' AS isReadyCopy from PA_IP_DATA_REGISTER ipr, PA_IP_DATA_WARDPATIENT ipw WHERE ipr.ENCOUNTERID = ipw.ENCOUNTERID and ipr.isdelete = '0'
AND ipr.HCNO = #{healthCardNo}
AND to_char(ipr.PATIENTID) = #{patientId}
AND ipr.IDNO = #{idCardNo}
AND to_char(ipr.ENCOUNTERID) = #{inpatientId}
